            "Next" : "LambdaOldVersion",
            "Parameters" : {
              "FunctionName" : "arn:aws:lambda:eu-west-1:548105126730:function:stackstate-main-account-main-region-LambdaFunction-1AIMOJSC4PKMM:$LATEST",
              "Payload" : {
                "Input" : {
                  "message" : "World"
                }
              }
            },
            "Resource" : "arn:aws:states:::lambda:invoke",
            "Type" : "Task"
