          "LambdaOldVersion" : {
            "Next" : "DynamoDB",
            "Parameters" : {
              "FunctionName" : "arn:aws:lambda:eu-west-1:548105126730:function:stackstate-main-account-main-region-LambdaFunction-1AIMOJSC4PKMM:1",
